parameters.UserFlowContinuationSubscriptionRequest/:
{
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "type": "AdaptiveCard",
    "version": "1.2",
    "body": [
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "width": 2,
                    "items": [
                        {
                            "type": "TextBlock",
                            "text": "Your Teams environment: @{triggerOutputs()?['body/admin_displayname']}",
                            "weight": "Bolder",
                            "size": "Large"
                        },
                        {
                            "type": "TextBlock",
                            "text": "Your @{triggerOutputs()?['body/admin_displayname']} power platform environment might get deleted if the business justification is rejected or if you do not provide a business justification.",
                            "wrap": true,
                            "weight": "Bolder",
                            "color": "Attention"
                        },
                        {
                            "type": "TextBlock",
                            "text": "Microsoft Dataverse for Teams is an exciting new capability that integrates Teams with the Power Platform. Using Dataverse for Teams, anyone can make and deploy apps, flows, and chatbots directly within Teams.\nNote: ​As this is a limited capability, please be mindful of others. Create just the environments that you need to support your business process\n\nIf the environment you just created is for a production use case, please provide a business justification. ",
                            "isSubtle": true,
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Business Justification",
                            "wrap": true,
                            "weight": "Bolder"
                        },
                        {
                            "type": "Input.Text",
                            "id": "myBusinessJustification",
                            "isMultiline": true
                        }
                    ]
                }
            ]
        }
    ],
    "actions": [
        {
            "type": "Action.Submit",
            "title": "Submit"
        }
    ],
    "-ms-tokens": {}
}
